mapper.select:
SELECT * FROM commodity


        AND release_time >= #{releaseTimeEarliest}


        AND release_time <= #{releaseTimeLatest}


        AND price >= #{priceMin}


        AND price <= #{priceMax}


        AND allow_bargaining = #{allowBargaining}



            AND description LIKE CONCAT('%', #{description}, '%')



LIMIT #{pageSize} OFFSET #{offset}
